<!DOCTYPE html><html lang="es"><head><meta charset="utf-8">   <title>Example of Zebra Table with twitter bootstrap</title>   <meta name="description" content="Creating a Zebra table with Twitter Bootstrap. Learn with example of a Zebra Table with Twitter Bootstrap.">  <link href="twitter-bootstrap-v2/docs/assets/css/bootstrap.css" rel="stylesheet"> </head><body><?phprequire_once 'inc/functions.php';# Datos de Conexión a la Base de datos# (Hay que ajustarlos a tus necesidades particulares)$servidor  = $GLOBALS['SERVER'];$usuario   = $GLOBALS['USUARIO'];$password  = $GLOBALS['PASS'];$basedatos = $GLOBALS['BD']; # Establecer la conexión a la Base de Datos$conexion = mysqli_connect($servidor,$usuario,$password,$basedatos); # Consulta SQL que devuelve los nombres de las tablas de la Base de Datos$tablas = mysqli_query($conexion,'SHOW TABLES') or die('Imposible mostrar tablas');if(!isset($_GET["t"])){    echo '<table class="table table-striped table-bordered table-condensed" cellpadding="0" cellspacing="0"><thead><tr><th>Table Name</th><th>API Link</th><th>View Table</th></thead><tbody>';    while($tabla = mysqli_fetch_row($tablas)) {    $nombreTabla = $tabla[0];            echo '<tr>';        echo '<td>';        echo "<a href='?t=".$nombreTabla."'>".$nombreTabla."</a>";        echo '</td>';        echo '<td>';        $urlNow = "http://".$_SERVER['HTTP_HOST'].":".$_SERVER['SERVER_PORT'].$_SERVER['PHP_SELF'];        $pathFolder = dirname($urlNow);        $urlJson = $pathFolder."/getjson.php?t=".$nombreTabla;        echo '<a href="'.$urlJson.'">';        echo $urlJson;        echo '</a>';        echo '</td>';        echo '<td>';        $urlJson = $pathFolder."/showTable.php?t=".$nombreTabla;        echo '<a href="'.$urlJson.'">';        echo "Show ".$nombreTabla;        echo '</a>';        echo '</td>';        echo '</tr>';    }     echo '</tbody></table><br />';}else{    while($tabla = mysqli_fetch_row($tablas)) {        $nombreTabla = $tabla[0];        if($nombreTabla==$_GET["t"]){    	            echo '<h3>Tabla: '.$nombreTabla.'</h3>';            # Consulta SQL que devuelve los campos de cada tabla            $campos = mysqli_query($conexion,'SHOW COLUMNS FROM '.$nombreTabla) or die('Imposible mostrar campos de '.$nombreTabla);            # Muestra como tabla HTML los detalles de los campos de la tabla correspondiente            $count = 1;            if(mysqli_num_rows($campos)) {                    echo '<form>';                    echo '<table class="table table-striped table-bordered table-condensed" cellpadding="0" cellspacing="0"><thead><tr><th>Campo</th><th>API Link</th><th>View Table</th><th>Select</th></thead><tbody>';                    while($detalles = mysqli_fetch_row($campos)) {                            echo '<tr>';                            echo '<td>';                            echo $detalles[0];                            echo '</td>';                            echo '<td>';                            $urlNow = "http://".$_SERVER['HTTP_HOST'].":".$_SERVER['SERVER_PORT'].$_SERVER['PHP_SELF'];                            $pathFolder = dirname($urlNow);                            $urlJson = $pathFolder."/getjson.php?t=".$nombreTabla."&c=".$detalles[0];                            echo '<a href="'.$urlJson.'">';                            echo $urlJson;                            echo '</a>';                            echo '</td>';                            echo '<td>';                            $urlJson = $pathFolder."/showTable.php?t=".$nombreTabla."&c=".$detalles[0];                            echo '<a href="'.$urlJson.'">';                            echo "Show ".$detalles[0];                            echo '</a>';                            echo '</td>';                            echo '<td>';                            echo '<input id="cbc" type="checkbox" disabled=false value='.$count.'>';                            echo '</td>';                            echo '</tr>';                            $count++;                    }                    echo '</tbody></table><br />';                    echo '<input type="button" value ="Custom" onclick="customSelect('.$count.')">';                    echo '</form>';                                }        }    }}#Cerrar la conexión a la Base de Datosmysqli_close($conexion);?>        <input id="cbc" type="checkbox" disabled="true" value='1'><script lang="javascript">    function customSelect(i){                for(h=1;h<i;h++){                 document.getElementById("cbc"+h).enable = true;                    }    }</script> </body></html>